export const productListReducers = (state={products:[]}, action) => {
    switch(action.type) {
        case 'PODUCT_LIST_REQUEST':
            return {loading:true, products:[]}

        case 'PODUCT_LIST_SUCCESS':
            return {loading:false, products: ction.payload}

        case 'PODUCT_LIST_FAIL':
            return {loading:false, error: ction.payload}

        default:
            return state
    }
}
